<template>
  <view v-if="show" class="y-loading" :class="useClass"
        :style="[circleStyle]">
  </view>
</template>

<script lang="ts" setup>
import {computed} from "vue";

const props = withDefaults(defineProps<{
  mode?: "circle" | "flower";
  color?: string;
  size?: number | string;
  show?: boolean;
}>(), {
  mode: "circle",
  color: "#c7c7c7",
  size: "34",
  show: true,
})
const useClass = computed(() => {
  return props.mode == 'circle' ? 'y-loading-circle' : 'y-loading-flower'
})
const circleStyle = computed(() => {
  let style = {
    width: uni.$y.addUnit(props.size),
    height: uni.$y.addUnit(props.size),
    borderColor: "",
  };
  if (props.mode == "circle") {
    style.borderColor = `#e4e4e4 #e4e4e4 #e4e4e4 ${props.color ? props.color : '#c7c7c7'}`;
  }
  return style;
})
</script>

<style lang="scss" scoped>
@import "../../libs/scss/style.components.scss";

.y-loading-circle {
  /* #ifndef APP-NVUE */
  display: inline-flex;
  /* #endif */
  vertical-align: middle;
  width: 28rpx;
  height: 28rpx;
  background: 0 0;
  border-radius: 50%;
  border: 2px solid;
  border-color: #e5e5e5 #e5e5e5 #e5e5e5 #8f8d8e;
  animation: y-circle 1s linear infinite;
}

.y-loading-flower {
  width: 20px;
  height: 20px;
  display: inline-block;
  vertical-align: middle;
  -webkit-animation: a 1s steps(12) infinite;
  animation: y-flower 1s steps(12) infinite;
  background: transparent url(data:image/svg+xml;base64,) no-repeat;
  background-size: 100%;
}

@keyframes y-flower {
  0% {
    -webkit-transform: rotate(0deg);
    transform: rotate(0deg);
  }

  to {
    -webkit-transform: rotate(1turn);
    transform: rotate(1turn);
  }
}

@keyframes y-circle {
  0% {
    transform: rotate(0);
  }

  100% {
    transform: rotate(360deg);
  }
}
</style>
